                            <Badge className={`text-xs ${getLengthBadgeColor(thread.length)}`}>
                              {lengthOptions[thread.length].name}
                            </Badge>
                            <span className="text-sm text-muted-foreground">
                              {thread.wordCount} words
                            </span>
                            <span className="text-sm text-muted-foreground">
                              {thread.createdAt}
                            </span>
                          </div>
                          <p className="text-sm text-muted-foreground line-clamp-2">
                            {thread.content.substring(0, 120)}...
                          </p>
                        </div>
                      </div>

                      <div className="flex items-center justify-between">
                        <div className="text-xs text-muted-foreground">
                          Copied {thread.copyCount} times
                        </div>
                        <div className="flex items-center gap-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleCopyRecentThread(thread.content, thread.id)}
                            data-testid={`copy-thread-${thread.id}`}
                          >
                            <Copy className="w-4 h-4" />
                            <span className="hidden sm:inline ml-1">Copy</span>
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => setViewingThread(thread)}
                            data-testid={`view-thread-${thread.id}`}
                          >
                            <Eye className="w-4 h-4" />
                            <span className="hidden sm:inline ml-1">View</span>
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleRegenerateThread(thread.topic, thread.length)}
                            data-testid={`regenerate-thread-${thread.id}`}
                          >
                            <RotateCcw className="w-4 h-4" />
                            <span className="hidden sm:inline ml-1">Regenerate</span>
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            className="text-red-500 hover:text-red-700"
                            onClick={() => handleDeleteThread(thread)}
                            data-testid={`delete-thread-${thread.id}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Thread View Modal */}
      {viewingThread && (
        <Dialog open={!!viewingThread} onOpenChange={() => setViewingThread(null)}>
          <DialogContent className="w-[95vw] max-w-4xl max-h-[85dvh] overflow-y-auto mx-auto p-4 sm:p-6 [&>button[aria-label='Close']]:hidden">
            <DialogHeader className="sticky top-0 z-10 bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/80 pb-4">
              <div className="flex items-start justify-between gap-3">
                <DialogTitle className="flex-1 min-w-0 text-base sm:text-lg leading-snug line-clamp-2 break-words pr-2">
                  {viewingThread.topic}
                </DialogTitle>
                <DialogClose asChild>
                  <Button variant="ghost" size="icon" aria-label="Close" className="shrink-0 -mr-2 sm:mr-0">
                    <X className="h-5 w-5" />
                  </Button>
                </DialogClose>
              </div>
              <div className="flex flex-wrap items-center gap-2 sm:gap-4 text-sm text-muted-foreground">
                <Badge className={`${getLengthBadgeColor(viewingThread.length)}`}>
                  {lengthOptions[viewingThread.length].name}
                </Badge>
                <span>{viewingThread.wordCount} words</span>
                <span>{viewingThread.tweetCount} tweets</span>
                <span>{viewingThread.createdAt}</span>
                <span>Copied {viewingThread.copyCount} times</span>
              </div>
            </DialogHeader>

            <div className="space-y-4">
              <div className="bg-muted/50 rounded-lg p-3 sm:p-6">
                <pre className="whitespace-pre-wrap text-xs sm:text-sm font-mono leading-relaxed break-words overflow-x-auto">
                  {viewingThread.content}
                </pre>
              </div>

              <div className="flex flex-col sm:flex-row gap-2">
                <Button
                  onClick={() => handleCopyRecentThread(viewingThread.content, viewingThread.id)}
                  className="flex items-center gap-2 w-full sm:w-auto justify-center sm:justify-start"
                >
                  <Copy className="w-4 h-4" />
                  Copy Thread
                </Button>
                <Button
                  variant="outline"
                  onClick={() => {
                    const element = document.createElement("a");
                    const file = new Blob([viewingThread.content], { type: 'text/plain' });
                    element.href = URL.createObjectURL(file);
                    element.download = `viral-thread-${viewingThread.topic.replace(/\s+/g, '-').toLowerCase()}.txt`;
                    document.body.appendChild(element);
                    element.click();
                    document.body.removeChild(element);
                  }}
                  className="flex items-center gap-2 w-full sm:w-auto justify-center sm:justify-start"
                >
                  <Download className="w-4 h-4" />
                  Download
                </Button>
                <Button
                  variant="outline"
                  onClick={() => {
                    handleRegenerateThread(viewingThread.topic, viewingThread.length);
                    setViewingThread(null);
                  }}
                  className="flex items-center gap-2 w-full sm:w-auto justify-center sm:justify-start"
                >
                  <RotateCcw className="w-4 h-4" />
                  Regenerate
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}

      {/* Delete Confirmation Dialog */}
      {deletingThread && (
        <Dialog open={!!deletingThread} onOpenChange={() => setDeletingThread(null)}>
          <DialogContent className="sm:max-w-md">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2 text-red-600">
                <Trash2 className="w-5 h-5" />
                Delete Thread
              </DialogTitle>
              <DialogDescription>
                Are you sure you want to delete "{deletingThread.topic}"? This action cannot be undone.
              </DialogDescription>
            </DialogHeader>
            <div className="flex gap-3 mt-6">
              <Button
                variant="outline"
                onClick={() => setDeletingThread(null)}
                className="flex-1"
              >
                Cancel
              </Button>
              <Button
                variant="destructive"
                onClick={confirmDeleteThread}
                className="flex-1 bg-red-600 hover:bg-red-700"
              >
                <Trash2 className="w-4 h-4 mr-2" />
                Delete
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

