import React, { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { HeaderSection } from "./sections/HeaderSection";
import { useAuth } from "@/contexts/AuthContext";
import { useToast } from "@/hooks/use-toast";
import { useSubscription } from "@/hooks/useSubscription";
import { 
  User, 
  Mail, 
  Calendar, 
  CreditCard, 
  Settings as SettingsIcon, 
  Save,
  Shield,
  Clock,
  Loader2
} from "lucide-react";

export default function Settings(): JSX.Element {
  const { user, updateProfile } = useAuth();
  const { toast } = useToast();

  // Split the user name into first and last name for editing
  const nameParts = (user?.name || "").split(" ");
  const [firstName, setFirstName] = useState(nameParts[0] || "");
  const [lastName, setLastName] = useState(nameParts.slice(1).join(" ") || "");
  const [email, setEmail] = useState(user?.email || "");
  const [isUpdating, setIsUpdating] = useState(false);
  const [isCreatingPortalSession, setIsCreatingPortalSession] = useState(false);

  // Update form when user data changes
  useEffect(() => {
    if (user) {
      const parts = user.name.split(" ");
      setFirstName(parts[0] || "");
      setLastName(parts.slice(1).join(" ") || "");
      setEmail(user.email);
    }
  }, [user]);

  // Fetch subscription data using existing hook
  const { subscription, isLoading: subscriptionLoading, isError: subscriptionError } = useSubscription();

  const handleSaveProfile = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsUpdating(true);

    try {
      const fullName = `${firstName.trim()} ${lastName.trim()}`.trim();
      const result = await updateProfile({
        email: email.trim(),
        name: fullName
      });

      if (result.success) {
        toast({
          title: "Profile updated successfully!",
          description: "Your profile information has been saved.",
        });
      } else {
        toast({
          title: "Update failed",
          description: result.error || "Failed to update profile. Please try again.",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Update failed",
        description: "An unexpected error occurred. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsUpdating(false);
    }
  };

  const handleManageBilling = async () => {
    setIsCreatingPortalSession(true);

    try {
      // Get auth token for API call
      const { getSupabaseClient } = await import("@/lib/supabase");
      const supabase = await getSupabaseClient();
      const { data: { session } } = await supabase.auth.getSession();

      if (!session?.access_token) {
        toast({
          title: "Authentication required",
          description: "Please sign in to manage your billing.",
          variant: "destructive",
        });
        return;
      }

      // Call your existing portal session API
      const response = await fetch('/api/create-portal-session', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${session.access_token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to create billing session');
      }

      const result = await response.json();

      if (result.url) {
        // Redirect to Stripe Customer Portal
        window.location.href = result.url;
      } else {
        throw new Error('No portal URL received');
      }

    } catch (error) {
      console.error('Error creating portal session:', error);
      toast({
        title: "Unable to access billing portal",
        description: error instanceof Error ? error.message : "Please try again later.",
        variant: "destructive",
      });
    } finally {
